SELECT * FROM(SELECT YEAR(SaleDate)[Year], ProductID, DATENAME(MONTH, SaleDate)[Month], AVG(Quantity)[Quantity] FROM dbo.SaleRecords WHERE ProductID = 1 GROUP BY YEAR(SaleDate), ProductID, DATENAME(MONTH, SaleDate)) AS MontlySalesData PIVOT(AVG([Quantity]) FOR Month IN([January],[February],[March],[April],[May],[June],[July],[August],[September],[October],[November],[December])) AS MNamePivot